mapper.select:
select count(1) from t_meeting_hotel t left join t_hotel h on t.hotel_id = h.id where t.meeting_id =#{meetingHotel.meetingId} and t.deleted =0 and h.deleted = 0
AND t.fee_name like CONCAT('%',#{meetingHotel.feeName},'%')
AND h.hotel_name like CONCAT('%',#{meetingHotel.hotelName},'%')
AND t.fee_type = #{meetingHotel.feeType}
AND t.parent_feeid = #{meetingHotel.parentFeeId}

select t.*,h.hotel_name from t_meeting_hotel t left join t_hotel h on t.hotel_id = h.id where t.meeting_id = #{meetingHotel.meetingId} and t.deleted =0 and h.deleted = 0
AND t.fee_name like CONCAT('%',#{meetingHotel.feeName},'%')
AND h.hotel_name like CONCAT('%',#{meetingHotel.hotelName},'%')
AND t.fee_type = #{meetingHotel.feeType}
AND t.parent_feeid = #{meetingHotel.parentFeeId}
order by t.create_time desc

SELECT
*
FROM t_meeting_hotel
WHERE meeting_id=#{meetingId} and hotel_id =#{hotelId} and deleted = 0

    AND fee_id =#{feeId}


    and date_time >= #{startTime}


    and date_time <= #{endTime}

SELECT
*
FROM t_meeting_hotel
where meeting_id =#{meetingId} and hotel_id =#{hotelId} and deleted = 0
and fee_id = #{feeId}

    and date_time =#{dateTime}

mapper.update:
update t_meeting_hotel set surplus_number =surplus_number + #{number} where meeting_id =#{meetingId}
and hotel_id =#{hotelId} and deleted = 0 and fee_id = #{feeId}
and date_time =#{dateTime}
